#include <stdio.h>
#include <string.h>

float CtoF(float celsius) {
    return (celsius * 1.8) + 32;
}

int flipNum(int num) {
    int reversed = 0;
    while (num != 0) {
        reversed = (reversed * 10) + (num % 10);
        num /= 10;
    }
    return reversed;
}

int BinToDec(char *binary) {
    int length = strlen(binary);
    int decimal = 0, base = 1;
    for (int i = length - 1; i >= 0; i--) {
        if (binary[i] == '1') {
            decimal += base;
        }
        base *= 2;
    }
    return decimal;
}

int is_binary(char *str) {
    for (int i = 0; i < strlen(str); i++) {
        if (str[i] != '0' && str[i] != '1') {
            return 0;
        }
    }
    return 1;
}

void clean_input(void) {
    int c;
    while ((c = getchar()) != '\n' && c != EOF);
}

int get_int_input() {
    int input;
    if (scanf("%d", &input) != 1) {
        printf("Erro: entrada não é um número\n");
        clean_input();
        input = -1;
    }
    return input;
}

int main() {
    int choice, input;
    char binary[100] = {0};
    float celsius;
    do {
        printf("Menu:\n");
        printf("1. Conversão Celsius para Fahrenheit\n");
        printf("2. Inverter números\n");
        printf("3. Conversão de binário para decimal\n");
        printf("0. Sair do programa\n");
        printf("Selecione uma opção: ");
        choice = get_int_input();
        switch (choice) {
            case 1:
                printf("Digite uma temperatura em Celsius: ");
                celsius = get_int_input();
                if (celsius != -1) {
                    printf("%.2f Celsius = %.2f Fahrenheit\n", celsius, CtoF(celsius));
                }
                break;
            case 2:
                printf("Digite um número: ");
                input = get_int_input();
                if (input != -1) {
                    printf("O número invertido é %d\n", flipNum(input));
                }
                break;
            case 3:
                printf("Digite um número binário: ");
                if (scanf("%s", binary) != 1) {
                    printf("Erro: entrada não");
                }
                break;
        }
    } while (choice != 0);
    return 0;
}

